import React, { createContext } from 'react';
import { Button } from './Button';
import axios from 'axios';

function InputForm() 
{
    let image = "";
    // const [setLoaded] = useState(false);
    let inputRef;

    const SaveImage = (e) => {
        e.preventDefault();

        if(e.target.files[0]){
            // setLoaded("loading")

                image =  e.target.files[0];

        }
        console.log("file input"); 
    }
    const SendImageToServer = async() => {
        if(image) {
            const formData = new FormData()
            formData.append('filename', image);

            await axios.post('http://localhost:8000/main/upload', formData)
            .then(function(response) {
                console.log(response);
            })
            alert("그림을 성공적으로 업로드했어요!");



            // setLoaded(false);
        }
        else {
            alert("그림이 업로드되지 않았어요. 다시 시도해주세요!");
        }
    }
    return (
        <>
            <li className='inputform__container'>
                <div className='inputform__input'>
                    <input type='file' 
                        accept='image/*' 
                        onChange={SaveImage}
                        ref={refParam => inputRef = refParam}
                        /* style={{ display: "none" }} */ 
                    />
                </div>
                <div className='inputform__button'>
                    <Button 
                        type='submit' 
                        onClick={SendImageToServer}
                    > 밀어서 잠금해제
                    </Button> 
                </div>
            </li>
        </>

    );
}

export default InputForm;
